import { ADDRESS_PREFIXES, ACCOUNT_NAME_LENGTH, SYSTEM_ADDRESSES } from '../constants';
import bs58check from '../encoding/base58check';
import { Buffer } from 'buffer';

let systemAddresses = [...SYSTEM_ADDRESSES];

export type AddressInput = Address | string | Buffer | Uint8Array;

/**
 * A wrapper around addresses. Internally addresses are stored and sent as raw bytes,
 * but client-side they are displayed as base58-check encoded strings.
 * The encoding requires some computation, so you should only convert address objects to strings when needed.
 */
export default class Address {
    value: Buffer;
    encoded?: string;
    isName: boolean;

    constructor(address: AddressInput) {
        this.isName = false;

        if (address instanceof Address) {
            // Just copy buffer
            this.value = Buffer.from(address.value);
        } else if (typeof address === 'string') {
            // Parse string
            if (address.length <= ACCOUNT_NAME_LENGTH || Address.isSystemName(address)) {
                this.value = Buffer.from(address);
                this.isName = true;
            } else {
                try {
                    this.value = Address.decode(address);
                } catch(e) {
                    throw new Error(`Address "${address}" could not be parsed as a base58-check encoded string and is not a valid name. ${e}`);
                }
            }
            this.encoded = address;
        } else if (address instanceof Buffer) {
            this.value = address;
        } else if (address instanceof Uint8Array) {
            // Treat array-like as buffer
            this.value = Buffer.from(address);
        } else {
            throw new Error(`Instantiate Address with raw bytes, a string in base58-check encoding, or a valid name, not ${address}`);
        }

        // Check for name encoded as bytes
        if (!this.isName) {
            const arrValue = Array.from(this.value);
            // Remove trailing 0s
            while (arrValue[arrValue.length-1] === 0) {
                arrValue.pop();
            }
            const buf = Buffer.from(arrValue);
            if (buf.length <= ACCOUNT_NAME_LENGTH || Address.isSystemName(buf.toString())) {
                this.isName = true;
                this.value = buf;
            }
        }
    }

    asBytes(): Uint8Array {
        return new Uint8Array(this.value);
    }

    get bytes(): Uint8Array {
        return this.asBytes();
    }

    toJSON(): string {
        return this.toString();
    }

    toString(): string {
        if (typeof this.encoded !== 'undefined' && this.encoded !== null) {
            return this.encoded;
        }

        if (this.isName) {
            this.encoded = Buffer.from(this.value).toString();
        } else {
            this.encoded = Address.encode(this.value);
        }
        return this.encoded;
    }

    /**
     * Decode bs58check string into bytes
     */
    static decode(bs58string: string): Buffer {
        const decoded = bs58check.decode(bs58string);
        if (decoded[0] !== ADDRESS_PREFIXES.ACCOUNT) throw new Error(`invalid address prefix (${decoded[0]})`);
        if (decoded.length !== 33 + 1) throw new Error(`invalid address length (${decoded.length-1})`);
        return Buffer.from(decoded.slice(1));
    }

    /**
     * Encode bytes into bs58check string
     */
    static encode(byteArray: Buffer): string {
        if (!byteArray || byteArray.length === 0) return ''; // return empty string for null address
        const buf = Buffer.from([ADDRESS_PREFIXES.ACCOUNT, ...byteArray]);
        return bs58check.encode(buf);
    }

    equal(_otherAddress: AddressInput): boolean {
        const otherAddress = _otherAddress instanceof Address ? _otherAddress : new Address(_otherAddress);
        return Address.valueEqual(this.value, otherAddress.value);
    }

    /**
     * Returns true if the address is empty, i.e. '' or empty buffer
     */
    isEmpty(): boolean {
        return this.value.length === 0;
    }

    get length(): number {
        return this.value.length;
    }

    isSystemAddress(): boolean {
        return this.isName && Address.isSystemName(this.toString());
    }

    static isSystemName(name: string): boolean {
        return systemAddresses.indexOf(name) !== -1;
    }

    static setSystemAddresses(addresses: string[]): void {
        systemAddresses = addresses;
    }

    private static valueEqual(a: Buffer, b: Buffer): boolean {
        return a.length == b.length && a.every((aElem, i) => aElem === b[i]);
    } 
}

export const ADDRESS_PREFIXES = {
    ACCOUNT: 0x42,
    CONTRACT: 0xC0,
    PRIVATE_KEY: 0xAA,
};

export const ACCOUNT_NAME_LENGTH = 12;

export const SYSTEM_ADDRESSES = ['aergo.system', 'aergo.name', 'aergo.enterprise', 'aergo.vault'];

export const UNITS = {
    NATIVE_TOKEN: {
        baseLabel: 'Aergo',
        baseDigits: 18,

        subUnits: [
            { e: 0, label: 'aer' },
            { e: 18, label: 'aergo' }
        ],

        unitSize: {
            'aergo': 18,
            'gaer': 9,
            'aer': 0
        }
    }
};

export const BIP44_ID = 441;

export const WALLET_HDPATH = `m/44'/${BIP44_ID}'/0'/0/`;

export default {
    ADDRESS_PREFIXES,
    UNITS,
    ACCOUNT_NAME_LENGTH,
    BIP44_ID,
    WALLET_HDPATH,
    SYSTEM_ADDRESSES,
};

/**
 * This is a Typescript port of the bs58check package
 */
import base58 from './base58';
import { BaseEncoder } from './base-x';
import { Buffer } from 'buffer';
import createHash from 'create-hash';

function bs58checkBase(checksumFn: ((data: Buffer) => Buffer)): BaseEncoder {
    // Encode a buffer as a base58-check encoded string
    function encode(payload: Buffer): string {
        const checksum = checksumFn(payload);
        return base58.encode(Buffer.concat([
            payload,
            checksum,
        ], payload.length + 4));
    }

    function decodeRaw(buffer: Buffer): Buffer | undefined {
        const payload = buffer.slice(0, -4);
        const checksum = buffer.slice(-4);
        const newChecksum = checksumFn(payload);

        if (checksum[0] ^ newChecksum[0] |
                checksum[1] ^ newChecksum[1] |
                checksum[2] ^ newChecksum[2] |
                checksum[3] ^ newChecksum[3]) return;

        return payload;
    }

    // Decode a base58-check encoded string to a buffer, no result if checksum is wrong
    function decodeUnsafe(string: string): Buffer | undefined {
        const buffer = base58.decodeUnsafe(string);
        if (!buffer) return;
        return decodeRaw(buffer);
    }

    function decode(string: string): Buffer {
        const buffer = base58.decode(string);
        const payload = decodeRaw(buffer);
        if (!payload) throw new Error('Invalid checksum');
        return payload;
    }

    return {
        encode: encode,
        decode: decode,
        decodeUnsafe: decodeUnsafe,
    };
}

// SHA256(SHA256(buffer))
function sha256x2(buffer: Buffer): Buffer {
    const tmp = createHash('sha256').update(buffer).digest();
    return createHash('sha256').update(tmp).digest();
}

export default bs58checkBase(sha256x2);

import baseX from './base-x';
const ALPHABET = '123456789ABCDEFGHJKLMNPQRSTUVWXYZabcdefghijkmnopqrstuvwxyz';
export default baseX(ALPHABET);

/**
 * This is a Typescript port of the base-x package
 */

// base-x encoding / decoding
// Copyright (c) 2018 base-x contributors
// Copyright (c) 2014-2018 The Bitcoin Core developers (base58.cpp)
// Distributed under the MIT software license, see the accompanying
// file LICENSE or http://www.opensource.org/licenses/mit-license.php.

import { Buffer } from 'buffer';

export interface BaseEncoder {
    encode(source: Buffer): string;
    decode(source: string): Buffer;
    decodeUnsafe(source: string): Buffer | undefined;
}

export default function base(ALPHABET: string): BaseEncoder {
    if (ALPHABET.length >= 255) throw new TypeError('Alphabet too long');

    const BASE_MAP = new Uint8Array(256);
    BASE_MAP.fill(255);

    for (let i = 0; i < ALPHABET.length; i++) {
        const x = ALPHABET.charAt(i);
        const xc = x.charCodeAt(0);

        if (BASE_MAP[xc] !== 255) throw new TypeError(x + ' is ambiguous');
        BASE_MAP[xc] = i;
    }

    const BASE = ALPHABET.length;
    const LEADER = ALPHABET.charAt(0);
    const FACTOR = Math.log(BASE) / Math.log(256); // log(BASE) / log(256), rounded up
    const iFACTOR = Math.log(256) / Math.log(BASE); // log(256) / log(BASE), rounded up

    function encode(source: Buffer): string {
        if (!Buffer.isBuffer(source)) throw new TypeError('Expected Buffer');
        if (source.length === 0) return '';

        // Skip & count leading zeroes.
        let zeroes = 0;
        let length = 0;
        let pbegin = 0;
        const pend = source.length;

        while (pbegin !== pend && source[pbegin] === 0) {
            pbegin++;
            zeroes++;
        }

        // Allocate enough space in big-endian base58 representation.
        const size = ((pend - pbegin) * iFACTOR + 1) >>> 0;
        const b58 = new Uint8Array(size);

        // Process the bytes.
        while (pbegin !== pend) {
            let carry = source[pbegin];

            // Apply "b58 = b58 * 256 + ch".
            let i = 0;
            for (let it = size - 1; (carry !== 0 || i < length) && (it !== -1); it--, i++) {
                carry += (256 * b58[it]) >>> 0;
                b58[it] = (carry % BASE) >>> 0;
                carry = (carry / BASE) >>> 0;
            }

            if (carry !== 0) throw new Error('Non-zero carry');
            length = i;
            pbegin++;
        }

        // Skip leading zeroes in base58 result.
        let it = size - length;
        while (it !== size && b58[it] === 0) {
            it++;
        }

        // Translate the result into a string.
        let str = LEADER.repeat(zeroes);
        for (; it < size; ++it) str += ALPHABET.charAt(b58[it]);

        return str;
    }

    function decodeUnsafe(source: string): Buffer | undefined {
        if (typeof source !== 'string') throw new TypeError('Expected String');
        if (source.length === 0) return Buffer.alloc(0);

        let psz = 0;

        // Skip leading spaces.
        if (source[psz] === ' ') return;

        // Skip and count leading '1's.
        let zeroes = 0;
        let length = 0;
        while (source[psz] === LEADER) {
            zeroes++;
            psz++;
        }

        // Allocate enough space in big-endian base256 representation.
        const size = (((source.length - psz) * FACTOR) + 1) >>> 0; // log(58) / log(256), rounded up.
        const b256 = new Uint8Array(size);

        // Process the characters.
        while (source[psz]) {
            // Decode character
            let carry = BASE_MAP[source.charCodeAt(psz)];

            // Invalid character
            if (carry === 255) return;

            let i = 0;
            for (let it = size - 1; (carry !== 0 || i < length) && (it !== -1); it--, i++) {
                carry += (BASE * b256[it]) >>> 0;
                b256[it] = (carry % 256) >>> 0;
                carry = (carry / 256) >>> 0;
            }

            if (carry !== 0) throw new Error('Non-zero carry');
            length = i;
            psz++;
        }

        // Skip trailing spaces.
        if (source[psz] === ' ') return;

        // Skip leading zeroes in b256.
        let it = size - length;
        while (it !== size && b256[it] === 0) {
            it++;
        }

        const vch = Buffer.allocUnsafe(zeroes + (size - it));
        vch.fill(0x00, 0, zeroes);

        let j = zeroes;
        while (it !== size) {
            vch[j++] = b256[it++];
        }

        return vch;
    }

    function decode(source: string): Buffer {
        const buffer = decodeUnsafe(source);
        if (buffer) return buffer;
        throw new Error('Non-base' + BASE + ' character');
    }

    return {
        encode: encode,
        decodeUnsafe: decodeUnsafe,
        decode: decode
    };
}

import { UNITS } from '../constants';
import { fromHexString, toHexString } from '../encoding';
import JSBI from 'jsbi';
import { Buffer } from 'buffer';

const DEFAULT_USER_UNIT = 'aergo';
const DEFAULT_NETWORK_UNIT = 'aer';

export type AmountArg = Amount | JSBI | number | string;

function getUnitPrecision(unit: string): number {
    const units = UNITS.NATIVE_TOKEN.unitSize;
    function isValidUnit(unit: string): unit is keyof typeof units {
        return unit in units;
    }
    if (!isValidUnit(unit)) {
        throw new TypeError(`unrecognized unit: ${unit}`);
    }
    return units[unit];
}

/**
 * A wrapper around amounts with units.
 * Over the network, amounts are sent as raw bytes.
 * In the client, they are exposed as BigInts, but also compatible with plain strings or numbers (if smaller than 2^31-1)
 * Uses 'aergo' as default unit when passing strings or numbers.
 * Uses 'aer' as default unit when passing BigInts, buffers or byte arrays.
 * Whenever you pass amounts to other functions, they will try to coerce them to BigInt using this class.
 */
export default class Amount {
    value!: Readonly<JSBI>; // value in base unit
    unit!: string; // unit for displaying

    private static _valueFromString(value: string, unit = ''): JSBI {
        if (unit === '') {
            unit = DEFAULT_USER_UNIT;
        }
        const prec = getUnitPrecision(unit);
        if (prec > 0) {
            value = Amount.moveDecimalPoint(value, prec);
        }
        return JSBI.BigInt(value);
    }

    constructor(value: AmountArg|Buffer|Uint8Array, unit = '', newUnit?: string) {
        if (value instanceof Amount) {
            return value;
        }
        if (typeof value === 'string') {
            const [amount, _unit] = value.split(' ', 2);
            unit = unit || _unit;
            this.value = Amount._valueFromString(amount, unit);
        } else if (typeof value === 'number') {
            this.value = Amount._valueFromString(''+value, unit);
        } else if (value instanceof JSBI) {
            if (typeof unit === 'undefined' || unit === '') {
                unit = DEFAULT_NETWORK_UNIT;
            }
            this.value = JSBI.BigInt(value);
        } else if (value instanceof Buffer || value instanceof Uint8Array) {
            if (typeof unit === 'undefined' || unit === '') {
                unit = DEFAULT_NETWORK_UNIT;
            }
            this.value = JSBI.BigInt(toHexString(value, true));
        } else {
            throw new Error(`Instantiate Amount with JSBI|number|string|Buffer|Uint8Array, not ${value} (${typeof value})`);
        }
        this.unit = unit;
        if (typeof this.unit === 'undefined' || this.unit === '') {
            this.unit = DEFAULT_USER_UNIT;
        }

        // Set new unit for displaying
        if (typeof newUnit !== 'undefined') {
            this.unit = newUnit;
        }

        // Freeze value. Otherwise some libraries mess this up since it is actually an Array subclass with a custom propery
        this.value = Object.freeze(this.value);
    }

    /**
     * Returns value as byte buffer
     */
    asBytes(): Buffer {
        return Buffer.from(fromHexString(this.value.toString(16)));
    }

    /**
     * JSON.stringifes to string with unit aer, which can be easily deserialized by new Amount(x)
     */
    toJSON(): string {
        return this.toUnit('aer').toString();
    }

    /**
     * Returns formatted string including unit
     */
    toString(): string {
        if (this.unit) {
            return `${this.formatNumber()} ${this.unit}`;
        }
        return `${this.formatNumber()}`;
    }

    /**
     * Move decimal point in string by digits, positive to the right, negative to the left.
     * This extends the string if necessary.
     * Example: ("0.0001", 4 => "1"), ("0.0001", -4 => "0.00000001")
     * @param str 
     * @param digits 
     */
    static moveDecimalPoint(str: string, digits: number): string {
        if (digits === 0 || str === '0') return str;
        if (str.indexOf('.') === -1) {
            str = str + '.';
        }
        let idx = str.indexOf('.');

        // Extend string to have enough space to move decimal point
        if (digits > str.length - idx) {
            str = str.padEnd(digits + idx + 1, '0');
        }
        if (digits < -idx) {
            str = str.padStart(str.length-idx-digits, '0');
        }

        // remove decimal point and reinsert at new location
        idx = str.indexOf('.');
        str = str.replace('.', '');
        str = str.substr(0, idx + digits) + '.' + str.substr(idx + digits);

        // remove trailing 0 and .
        str = str.replace(/\.?0*$/, '');
        // remove leading 0
        str = str.replace(/^0+/, '');
        // add leading 0 before .
        str = str.replace(/^\./, '0.');
        return str;
    }
    formatNumber(unit = ''): string {
        if (unit === '') unit = this.unit;
        if (unit === '') return this.value.toString();
        const prec = getUnitPrecision(unit);
        return Amount.moveDecimalPoint(this.value.toString(), -prec);
    }

    /**
     * Convert to another unit
     * @param unit string (aer, gaer, aergo)
     */
    toUnit(unit: string): Amount {
        return new Amount(this.value as JSBI, '', unit);
    }

    /**
     * Convert arg into JSBI value
     * Can optionally provide a defaultUnit that is used if arg does not contain a unit.
     */
    static toJSBI(arg: AmountArg, defaultUnit = ''): JSBI {
        if (!(arg instanceof Amount)) {
            const [amount, _unit] = `${arg}`.split(' ', 2);
            const unit = _unit || defaultUnit;
            arg = new Amount(amount, unit);
        }
        return JSBI.BigInt(arg.value);
    }

    /**
     * Compare this amount with other amount.
     * If otherAmount has no unit, assumes unit of this amount.
     * this >  other -> +1
     * this  < other -> -1
     * this == other -> 0
     * @param otherAmount 
     */
    compare(otherAmount: AmountArg): number {
        const a = this.value as JSBI;
        const b = Amount.toJSBI(otherAmount, this.unit);
        return JSBI.equal(a, b) ? 0 : JSBI.lessThan(a, b) ? -1 : 1;
    }

    /**
     * Return true if otherAmount is equal to this amount.
     * @param otherAmount 
     */
    equal(otherAmount: AmountArg): boolean {
        return this.compare(otherAmount) === 0;
    }

    /**
     * Add another amount to amount.
     * If otherAmount has no unit, assumes unit of this amount.
     * 10 aergo + 10 = 20 aergo
     * 10 aer + 10 = 20 aer
     * 10 aergo + 10 aer = 10.00000000000000001 aergo
     * @param otherAmount 
     */
    add(otherAmount: AmountArg): Amount {
        const sum = JSBI.add(this.value as JSBI, Amount.toJSBI(otherAmount, this.unit));
        return new Amount(sum, this.unit);
    }

    /**
     * Subtract another amount from amount.
     * If otherAmount has no unit, assumes unit of this amount.
     * 10 aergo - 5 = 5 aergo
     * 10 aer - 5 = 5 aer
     * 1 aer - 1 aergo = -999999999999999999 aer
     * @param otherAmount 
     */
    sub(otherAmount: AmountArg): Amount {
        const sum = JSBI.subtract(this.value as JSBI, Amount.toJSBI(otherAmount, this.unit));
        return new Amount(sum, this.unit);
    }

    /**
     * Divide amount by another amount.
     * Warning: double check your units. The division is based on the aer value, so
     * if your otherAmount has a unit, it will be converted to aer.
     * This function tries to do the right thing in regards to dividing units:
     * 10 aergo / 10 = 1 aergo  (keep unit)
     * 10 aergo / 10 aergo = 1  (unit-less)
     * 1 aer / 2 aer = 0  (truncation of sub 1 aer amount)
     * @param otherAmount 
     */
    div(otherAmount: AmountArg): Amount {
        let newUnit;
        const sum = JSBI.divide(this.value as JSBI, Amount.toJSBI(otherAmount, 'aer'));
        // if both amounts had units, the result should be unit-less
        let otherHasUnit = (otherAmount instanceof Amount) && Boolean(otherAmount.unit);
        if (!otherHasUnit && typeof otherAmount === 'string') {
            const [, _unit] = `${otherAmount}`.split(' ', 2);
            otherHasUnit = Boolean(_unit);
        }
        if (otherHasUnit) {
            newUnit = '';
        }
        return new Amount(sum, this.unit, newUnit);
    }

    /**
     * Multiply amount by another amount.
     * Warning: double check your units. The multiplication is based on the aer value, so
     * if your otherAmount has a unit, it will be converted to aer.
     * However, while the value is correct, there's no way to display unit^2.
     * 10 aergo * 10 aergo = 10 * 10^18 aer * 10 * 10^18 aer = 100 * 10^36 aer = 100 * 10^18 aergo
     * 10 aergo * 10 = 10 * 10^18 aer * 10 = 100 * 10^18 aer = 100 aergo
     * @param otherAmount 
     */
    mul(otherAmount: AmountArg): Amount {
        const sum = JSBI.multiply(this.value as JSBI, Amount.toJSBI(otherAmount, 'aer'));
        return new Amount(sum, this.unit);
    }
}

export * from './bytes';
export { default as base58 } from './base58';
export { default as base58check } from './base58check';

import bs58 from './base58';
import { Buffer } from 'buffer';

export type StringOrBuffer = string | Buffer | Uint8Array;
export type ByteEncoding = 'base58' | BufferEncoding;

export function encodeBuffer(val: Buffer | Uint8Array, enc: ByteEncoding = 'base58'): string  {
    if (enc === 'base58') {
        return bs58.encode(Buffer.from(val));
    }
    return Buffer.from(val).toString(enc);
}

/**
 * If input is a string, use `enc` to decode string (default: base58).
 * Otherwise, just return Buffer.
 */
export function decodeToBytes(val: StringOrBuffer, enc: ByteEncoding = 'base58'): Buffer {
    if (typeof val === 'string') {
        if (enc === 'base58') {
            return bs58.decode(val);
        }
        return Buffer.from(val, enc);
    }
    return Buffer.from(val);
}

interface NumberIterable<T> {
    [Symbol.iterator](): Iterator<T>;
}
interface HexConvertible {
    reduce: <U, T = this>(callbackfn: (previousValue: U, currentValue: number, currentIndex: number, array: NumberIterable<T>) => U, initialValue: U) => U;
}
/**
 * @param bytes anything to a hex string that has an iterable that returns numbers and a reduce method, e.g. number[], Uint8Array, Buffer
 * @param format add the string '0x' in front of the output
 */
export const toHexString = function(bytes: HexConvertible, format=false): string {
    const result = bytes.reduce((str, byte) => str + byte.toString(16).padStart(2, '0'), '');
    if (!format) return result;
    if (result === '00' || result === '') return '0x0';
    return '0x' + result;
};

export const fromHexString = function(hexString: string): Uint8Array {
    if (hexString.length === 0) return Uint8Array.from([]);
    if (hexString.length % 2 === 1) hexString = '0' + hexString;
    const match = hexString.match(/.{1,2}/g);
    if (!match) throw new Error('cannot parse string as hex');
    return new Uint8Array(match.map(byte => parseInt(byte, 16)));
};

/**
 * Convert number to Uint8Array
 * @param d 
 * @param length 
 */
export const fromNumber = (d: number, length = 8): Uint8Array => {
    if (d >= Math.pow(2, length*8)) {
        throw new Error('Number exeeds range');
    }
    const arr = new Uint8Array(length);
    for (let i=0, j=1; i<8; i++, j *= 0x100) {
        arr[i] = (d / j) & 0xff;
    }
    return arr;
};

/**
 * TODO: what's this? Is this useful?
 */
export const toBytesUint32 = (num: number): ArrayBuffer => {
    const arr = new ArrayBuffer(8);
    const view = new DataView(arr);
    view.setUint32(0, num, true);
    return arr;
};

/**
 * Returns the next interval to use for exponential backoff.
 * This curve yields every value 4 times before doubling in the next step.
 * The function is :code:`multiplier * 2**Math.floor(n/4)`.
 * By default (multiplier = 1s), the intervals reach ca. 1 minute (total time elapsed ca. 4 minutes) after step 24,
 * so it is advised to declare a timeout after a certain number of steps.
 * @param n step on the interval curve
 * @param multiplier multiplier, default 1000 (1s)
 */
export function backoffIntervalStep(n: number, multiplier = 1000): number {
    return multiplier * 2**Math.floor(n/4);
}

/**
 * A promisified version of setTimeout
 * @param ms 
 */
export function waitFor(ms: number): Promise<void> {
    return new Promise(resolve => {
        setTimeout(resolve, ms);
    });
}

/**
 * Retry calling an async function until it does not throw a matching error.
 * If it throws a non-matching error, re-raise that, otherwise return the result.
 * @param fn async function that returns a result.
 * @param matchError error match function. Will retry as long as this function returns true.
 * @param timeout timeout in ms, will re-raise last error when next try would exceed this time. 0 (default) means no timeout is applied.
 * @param baseBackoffInterval basis used for exponential backoff intervall calculation
 */
export function retryIfErrorMatch<T>(
    fn: () => Promise<T>,
    matchError: (e: any) => boolean,
    timeout: number = 0,
    baseBackoffInterval = 500,
): Promise<T> {
    const started = new Date();
    let retryCount = 0;
    const retryLoad = async (): Promise<T> => {
        try {
            return await fn();
        } catch(e) {
            if (matchError(e) === false) {
                throw e;
            }
            const interval = backoffIntervalStep(retryCount++, baseBackoffInterval);
            if (timeout) {
                const elapsed = +new Date() - (+started);
                if (elapsed + interval >= timeout) {
                    const unit = elapsed < 1000 ? 'ms' : 's';
                    const elapsedFormat = elapsed < 1000 ? elapsed : Math.round(elapsed / 100) / 10;
                    throw new Error(`timeout after ${elapsedFormat}${unit}: ${e}`);
                }
            }
            await waitFor(interval);
            return await retryLoad();
        }
    };
    return retryLoad();
}
